import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Login from './components/Login'; // Login component for authentication
import Dashboard from './components/Dashboard'; // Dashboard where users see their exercises and progress

function App() {
  const [user, setUser] = useState(null); // Track the logged-in user

  // This effect simulates checking for an authenticated user
  useEffect(() => {
    const loggedUser = localStorage.getItem('user'); // Simulate session
    if (loggedUser) {
      setUser(JSON.parse(loggedUser));
    }
  }, []);

  return (
    <Router>
      <div className="App">
        <Routes>
          {/* Route to Login Page */}
          <Route exact path="/">
            {user ? (
              <Dashboard user={user} setUser={setUser} />
            ) : (
              <Login setUser={setUser} />
            )}
          </Route>

          {/* Fallback for undefined routes */}
          <Route path="*">
            <NotFound />
          </Route>
        </Routes>
      </div>
    </Router>
  );
}

export default App;
